mapper.select:
SELECT 

FROM bckj_biz_article a

WHERE a.owid = #{owid}

SELECT 

FROM bckj_biz_article a






		ORDER BY ${page.orderBy}

	        SELECT

		FROM bckj_biz_article a



                AND a.wzbt LIKE concat('%',#{wzbt},'%')


                AND a.fbsj > #{fbsj1}


                AND a.fbsj < #{fbsj2}


                AND a.sxsj > #{sxsj1}


                AND a.sxsj < #{sxsj2}


                AND a.lmbh = #{lmbh}



                AND a.wzly = LIKE concat('%',#{wzly},'%')


                AND a.fbr = #{fbr}


                AND a.wzzt = #{wzzt}




				ORDER BY ${orderBy}

        SELECT
        a.wzbt AS "wzbt",a.owid AS "owid",a.fbsj AS "fbsj",a.jjnr AS "jjnr",a.tpjj AS "tpjj"
        FROM bckj_biz_article a

			( a.sxsj > SYSDATE() OR a.sxsj is NULL)

                AND a.lmbh = #{lmbh}


                AND a.wzzt = #{wzzt}


				AND (a.gjz LIKE concat('%',#{gjz},'%')  OR a.wzbt  LIKE concat('%',#{gjz},'%'))


				${sxh}


        ORDER BY ${orderBy}

SELECT 

FROM bckj_biz_article a






		ORDER BY ${page.orderBy}
