mapper.select:
SELECT a.owid as owid from
bckj_biz_yhxx a, bckj_biz_yhkz b

    a.owid=b.yh_ref_owid

        and a.sfz=#{sfz}


        and b.xsxh=#{xsxh}


        and a.xm=#{xsxm}

SELECT
  a.owid AS owid,
  a.xm AS xm,
  a.sjh AS sjh,
  c.xsxh AS xsxh,
  c.xsxy AS xsxy,
  c.xszy AS xszy,
  c.xsbj AS xsbj,
  d.gzsj AS gzsj,
  d.exp10 AS exp10,
  d.state AS state
from bckj_biz_yhkz c,bckj_biz_xsgz d, bckj_biz_yhxx a , bckj_biz_job b

     a.owid=c.yh_ref_owid and a.yhlx=1 and c.olx=0 and b.owid = d.job_ref_owid and d.yh_ref_owid=a.owid and d.xxlb=1

        and c.xsxh=#{xsxh}


        and a.xm=#{xm}


        and c.xsxy=#{xsxh}


        and c.xsbj=#{xsbj}


        and d.xszy=#{xszy}


         and a.sjh=#{sjh}


ORDER BY c.xsxh asc

