mapper.select:
SELECT

FROM bckj_biz_jybm a

WHERE a.owid = #{owid}

SELECT

FROM bckj_biz_jybm a






        ORDER BY ${page.orderBy}

        SELECT
        b.zwbt AS "zwbt",
        b.zw_pro AS "zwPro",
        b.zw_city AS "zwCity",
        b.zw_area AS "zwArea",
        b.zph_jbdd AS "zphJbdd",
        b.zph_ksrq AS "zphKsrq",
        b.zph_jtsj AS "zphJtsj",
        b.exp1 AS "gsqymc",
        b.exp3 AS "xxlxr",
        b.exp4 AS "xxlxrdh",
		b.exp6 AS "qyLxr",
		b.exp7 AS "qyLxrdh",

        FROM bckj_biz_job b
        RIGHT JOIN bckj_biz_jybm a ON a.job_ref_owid = b.owid


                AND b.zwbt like CONCAT('%', #{zwbt},'%')


                AND b.exp1 like CONCAT('%', #{gsqymc},'%')


                AND a.qyxx_ref_owid = #{qyxxRefOwid}


                AND a.job_ref_owid = #{jobRefOwid}


				AND a.lxr = #{lxr}


                AND a.yh_ref_owid = #{yhRefOwid}


                AND a.state = #{state}


                AND a.bmlx = #{bmlx}


                AND a.bmdx = #{bmdx}


                AND a.qymc = #{qymc}


                AND a.qysh = #{qysh}


                AND a.zdytj1 like CONCAT('%',#{zdytj1},'%')


                AND a.zdytj2 like CONCAT('%',#{zdytj2},'%')


                AND a.zdytj3 like CONCAT('%',#{zdytj3},'%')


                AND a.zdytj4 like CONCAT('%',#{zdytj4},'%')


                AND a.zdytj5 like CONCAT('%',#{zdytj5},'%')




                ORDER BY ${orderBy}

SELECT

FROM bckj_biz_jybm a


        AND a.qyxx_ref_owid = #{qyxxRefOwid}


        AND a.job_ref_owid = #{jobRefOwid}


        AND a.yh_ref_owid = #{yhRefOwid}


        AND a.state = #{state}


        AND (a.state = 0 or a.state = 2 )


        AND a.bmlx = #{bmlx}


        AND a.bmdx = #{bmdx}


        AND a.qymc like CONCAT('%',#{qymc},'%')


        AND a.qysh like CONCAT('%',#{qysh},'%')


        AND a.xjsj >= #{xjsj1} AND a.xjsj <= #{xjsj2}


        AND a.zdytj1 like CONCAT('%',#{zdytj1},'%')


        AND a.zdytj2 like CONCAT('%',#{zdytj2},'%')


        AND a.zdytj3 like CONCAT('%',#{zdytj3},'%')


        AND a.zdytj4 like CONCAT('%',#{zdytj4},'%')


        AND a.zdytj5 like CONCAT('%',#{zdytj5},'%')


        AND a.jkr like CONCAT('%',#{jkr},'%')




        ORDER BY ${orderBy}

SELECT

FROM bckj_biz_jybm a






        ORDER BY ${page.orderBy}

select

from
bckj_biz_jybm a


        AND a.qyxx_ref_owid = #{qyxxRefOwid}


        AND a.job_ref_owid = #{jobRefOwid}


        AND a.yh_ref_owid = #{yhRefOwid}
