mapper.select:
SELECT

FROM bckj_biz_qyxx a

WHERE a.owid = #{owid}

SELECT

FROM bckj_biz_qyxx a



        AND a.qy_tysh = #{qyTysh}


        AND a.qy_frsfz LIKE concat('%',#{qyFrsfz,jdbcType=VARCHAR},'%') ESCAPE '&'


        AND a.qymc = #{qymc}


        AND a.state = #{state}

SELECT

FROM bckj_biz_qyxx a






        ORDER BY ${page.orderBy}

        SELECT

        FROM bckj_biz_qyxx a



                AND a.qy_lxr = #{qyLxr}


                AND a.qy_lxrdh = #{qyLxrdh}


                AND a.state = #{state}


                AND a.state !=1 AND a.state !=3


				AND a.qymc LIKE concat('%',#{qymc,jdbcType=VARCHAR},'%') ESCAPE '&'


                AND a.qy_tysh LIKE concat('%',#{qyTysh,jdbcType=VARCHAR},'%') ESCAPE '&'


                AND a.qy_hylb = #{qyHylb}


                AND a.qy_gsgm = #{qyGsgm}


                AND a.qy_gsxz = #{qyGsxz}


                AND a.qy_prov LIKE concat('%',#{qyProv,jdbcType=VARCHAR},'%') ESCAPE '&'


                AND a.qy_city LIKE concat('%',#{qyCity,jdbcType=VARCHAR},'%') ESCAPE '&'


                AND a.qy_area LIKE concat('%',#{qyArea,jdbcType=VARCHAR},'%') ESCAPE '&'




                ORDER BY ${orderBy}

SELECT

FROM bckj_biz_qyxx a






        ORDER BY ${page.orderBy}

