mapper.select:
SELECT

FROM bckj_biz_xsgz a

WHERE a.owid = #{owid}

SELECT

FROM bckj_biz_xsgz a






        ORDER BY ${page.orderBy}

SELECT
b.exp1 AS "gsqymc",
b.zwbt AS "zwbt",

FROM bckj_biz_xsgz a
LEFT JOIN bckj_biz_job b ON a.job_ref_owid = b.owid



        AND b.zwbt like CONCAT('%', #{zwbt},'%')


        AND b.exp1 like CONCAT('%', #{gsqymc},'%')


        AND a.job_ref_owid = #{jobRefOwid}


        AND a.yh_ref_owid = #{yhRefOwid}


        AND a.gzlx = #{gzlx}


        AND a.xxlb = #{xxlb}


        AND a.state = #{state}




        ORDER BY ${orderBy}

SELECT

FROM bckj_biz_xsgz a






        ORDER BY ${page.orderBy}

select

from bckj_biz_xsgz a
where a.job_ref_owid=#{jobRefOwid} and a.yh_ref_owid=#{yhRefOwid} and a.xxlb=#{xxlb}

SELECT a.owid AS "owid", a.job_ref_owid AS "jobRefOwid", a.yh_ref_owid AS "yhRefOwid", a.gzlx AS "gzlx", a.xxlb AS "xxlb", a.gzsj AS "gzsj", a.lxdh AS "lxdh", a.lxr AS "lxr", a.exp1 as exp1, a.delflg AS "delflg", a.state AS "state" FROM bckj_biz_xsgz a
and a.xxlb =#{xxlb}
and a.job_ref_owid =#{jobRefOwid}
and a.gzlx =#{gzlx}
ORDER BY ${orderBy}
