mapper.select:
SELECT
a.*
FROM
ourway_sys_employs a


         AND a.owid = #{empOwid}


        AND a.emp_weixin = #{empWeixin}


        AND a.emp_type = #{empType}


        AND a.emp_card = #{empCard}


        AND a.emp_mobile = #{empMobile1}


        AND a.emp_id = #{empId}

SELECT
    count(a.owid)
FROM
ourway_sys_employs a


        AND a.emp_weixin = #{empWeixin}


        AND a.emp_card = #{empCard}


        AND a.emp_id = #{empId}


        AND (a.emp_tel = #{empTel}  OR  a.emp_id = #{empTel})

mapper.update:
UPDATE
   ourway_sys_files
SET
    file_class_id = #{newOwid}
WHERE
    file_class_id = #{oldOwid}

mapper.select:
select count(*)
from bckj_biz_qyxx


        and state=#{state}


        and year(createtime)=#{year}


        and month(createtime)=#{month}


        and qy_hylb=#{qyHylb}


        and qy_gsgm=#{qyGsgm}

select count(*)
from bckj_biz_job


        and state=#{state}


        and year(createtime)=#{year}


        and month(createtime)=#{month}


        and zwlx=#{zwlx}

select count(*) as jobNumber, a.job_ref_owid, b.zwbt, b.exp1
from bckj_biz_xsgz a, bckj_biz_job b
 1=1 and a.job_ref_owid=b.owid

        and a.gzlx=#{type}


        and year(a.createtime)=#{year}


group by a.job_ref_owid

    limit #{limit}

SELECT a.prov AS province
FROM bckj_biz_yhxx a


        AND YEAR(a.createtime)=#{year}

SELECT count(*) AS value, a.exp1 AS name FROM bckj_biz_jyscheme a
AND YEAR(a.createtime)=#{year}
GROUP BY a.exp1

SELECT a.* FROM ourway_sys_dic_value a,ourway_sys_dic b WHERE a.dic_ref_owid=b.owid

    AND b.TYPE=#{dicType}


    AND a.dic_val1=#{dicVal1}


    AND a.dic_val3=#{dicVal3}

select * from ourway_sys_dic a,ourway_sys_dic_value b
a.owid=b.dic_ref_owid
    and a.type = #{dicType}

        AND b.dicVal1 = #{dicVal1}
