mapper.select:
SELECT
a.*
FROM
ourway_sys_employs a


         AND a.owid = #{empOwid}


        AND a.emp_weixin = #{empWeixin}


        AND a.emp_type = #{empType}


        AND a.emp_card = #{empCard}


        AND a.emp_mobile = #{empMobile1}


        AND a.emp_id = #{empId}

SELECT
    count(a.owid)
FROM
ourway_sys_employs a


        AND a.emp_weixin = #{empWeixin}


        AND a.emp_card = #{empCard}


        AND a.emp_id = #{empId}


        AND (a.emp_tel = #{empTel}  OR  a.emp_id = #{empTel})

mapper.update:
UPDATE
   ourway_sys_files
SET
    file_class_id = #{newOwid}
WHERE
    file_class_id = #{oldOwid}

mapper.select:
SELECT a.zwbt, a.zph_ksrq AS ksrq FROM bckj_biz_job a a.state = 2 AND a.zph_ksrq >= CURRENT_DATE
AND a.zwlx=#{zwlx}
ORDER BY zph_ksrq

SELECT COUNT(*) AS value, a.state FROM bckj_biz_qyxx a a.state=1 OR a.state=2
AND YEAR(createtime)=#{year}
GROUP BY a.state

select count(*)
from bckj_biz_qyxx a
a.state=1 OR a.state=2

        AND YEAR(createtime)=#{year}


GROUP BY ${groupBy}

SELECT COUNT(*) AS value, zw_gzzn AS gzzn
FROM bckj_biz_job
 state = 2 AND zw_gzzn IS NOT NULL

        AND zwlx=#{zwlx}


        AND YEAR(createtime)=#{year}



    GROUP BY zw_gzzn

SELECT COUNT(*)
FROM bckj_biz_job
state=2

        AND zwlx=#{zwlx}


        AND YEAR(createtime)=#{year}


        AND month(createtime)=#{month}

SELECT COUNT(*)
FROM bckj_biz_jybm


        AND bmlx=#{bmlx}


        AND bmdx=#{bmdx}


        AND YEAR(createtime)=#{year}

SELECT COUNT(*)
FROM bckj_biz_xsgz


        AND gzlx=#{gzlx}


        AND xxlb=#{xxlb}


        AND YEAR(createtime)=#{year}

SELECT COUNT(*) AS value
FROM bckj_biz_job
state=2 AND zph_ksrq >= CURRENT_DATE

        AND zwlx=#{zwlx}


        AND YEAR(createtime)=#{year}

SELECT COUNT(*) AS value
FROM bckj_biz_job
 state=2 AND zph_ksrq < CURRENT_DATE

        AND zwlx=#{zwlx}


        AND YEAR(createtime)=#{year}

SELECT a.* FROM ourway_sys_dic_value a,ourway_sys_dic b WHERE a.dic_ref_owid=b.owid

    AND b.TYPE=#{dicType}


    AND a.dic_val1=#{dicVal1}


    AND a.dic_val3=#{dicVal3}

select * from ourway_sys_dic a,ourway_sys_dic_value b
a.owid=b.dic_ref_owid
    and a.type = #{dicType}

        AND b.dicVal1 = #{dicVal1}
