WHERE a.owid=b.dic_ref_owid and a.type=70003 and b.dic_val6=#{owid}

SELECT DISTINCT b.xsxy as xsxy
from bckj_biz_yhxx a,bckj_biz_yhkz b,bckj_biz_xsgz c
